package co.rysr.rysr.intro;

import android.app.Activity;
import android.content.Intent;
import android.net.Uri;
import android.os.Bundle;
import android.support.v4.app.Fragment;
import android.util.Log;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.Button;
import android.widget.Toast;

import com.braintreepayments.api.dropin.BraintreePaymentActivity;
import com.braintreepayments.api.dropin.Customization;

import butterknife.Bind;
import butterknife.ButterKnife;
import co.rysr.rysr.R;
import co.rysr.rysr.Utils.PaymentUtil;


/**
 * A simple {@link Fragment} subclass.
 * Activities that contain this fragment must implement the
 * {@link PaymentSetupFragment.OnFragmentInteractionListener} interface
 * to handle interaction events.
 * Use the {@link PaymentSetupFragment#newInstance} factory method to
 * create an instance of this fragment.
 */
public class PaymentSetupFragment extends Fragment {
    private static final int REQUEST_PAYMENT_ACTIVITY = 1;


    private OnFragmentInteractionListener mListener;

    @Bind(R.id.button) Button buttonSetup;

    /**
     * Use this factory method to create a new instance of
     * this fragment using the provided parameters.
     * @return A new instance of fragment PaymentSetupFragment.
     */
    public static PaymentSetupFragment newInstance() {
        return new PaymentSetupFragment();
    }

    public PaymentSetupFragment() {
        // Required empty public constructor
    }

    @Override
    public void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
    }

    @Override
    public View onCreateView(LayoutInflater inflater, ViewGroup container,
                             Bundle savedInstanceState) {
        // Inflate the layout for this fragment
        View view = inflater.inflate(R.layout.fragment_payment_setup, container, false);
        ButterKnife.bind(this, view);

        buttonSetup.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View view) {
                onBraintreeSubmit();
            }
        });
        return view;
    }

    // TODO: Rename method, update argument and hook method into UI event
    public void onButtonPressed(Uri uri) {
        if (mListener != null) {
            mListener.onPaymentSetupComplete();
        }
    }

    @Override
    public void onAttach(Activity activity) {
        super.onAttach(activity);
        try {
            mListener = (OnFragmentInteractionListener) activity;
        } catch (ClassCastException e) {
            throw new ClassCastException(activity.toString()
                    + " must implement OnFragmentInteractionListener");
        }
    }

    @Override
    public void onDetach() {
        super.onDetach();
        mListener = null;
    }

    /**
     * This interface must be implemented by activities that contain this
     * fragment to allow an interaction in this fragment to be communicated
     * to the activity and potentially other fragments contained in that
     * activity.
     * <p/>
     * See the Android Training lesson <a href=
     * "http://developer.android.com/training/basics/fragments/communicating.html"
     * >Communicating with Other Fragments</a> for more information.
     */
    public interface OnFragmentInteractionListener {
        void onPaymentSetupComplete();
    }

    /**
     * Launch the payment processor window with $0.00 in charges
     */
    public void onBraintreeSubmit() {
        Intent intent = new Intent(getActivity(), BraintreePaymentActivity.class);
//        intent.putExtra(BraintreePaymentActivity.EXTRA_CLIENT_TOKEN, "");
        intent.putExtra(BraintreePaymentActivity.EXTRA_CLIENT_TOKEN, PaymentUtil.getInstance().getClientToken());

        Customization customization = new Customization.CustomizationBuilder()
                .primaryDescription("Setup Payment Method")
//                .secondaryDescription("")
                .amount("$0.00")
                .submitButtonText("Confirm Payment Method")
                .build();
        intent.putExtra(BraintreePaymentActivity.EXTRA_CUSTOMIZATION, customization);
        startActivityForResult(intent, REQUEST_PAYMENT_ACTIVITY);
    }

    @Override
    public void onActivityResult(int requestCode, int resultCode, Intent data) {
        if (requestCode == REQUEST_PAYMENT_ACTIVITY) {
            if (resultCode == BraintreePaymentActivity.RESULT_OK) {
                String paymentMethodNonce = data.getStringExtra(BraintreePaymentActivity.EXTRA_PAYMENT_METHOD_NONCE);
                PaymentUtil.getInstance().saveNonce(paymentMethodNonce);
                Log.i("RYSR", paymentMethodNonce);
                buttonSetup.setEnabled(false);
                mListener.onPaymentSetupComplete();
                //postNonceToServer(paymentMethodNonce);
            } else {
                Toast.makeText(getActivity(), "An error was encountered.", Toast.LENGTH_SHORT).show();
            }
        }
    }

}
